$(document).ready(()=>{$("#submitCityName").on("click",t=>{t.preventDefault();const a="https://api.openweathermap.org/data/2.5/forecast?q="+$("#forcastLocation").val()+"&APPID=94d5b3ebbc302231ae85460cfe0af984";$.ajax({url:a,method:"GET",success:t=>{console.log(t),$("#forecast").append(`<div class="forecast-banner">\n\t\t\t\t<h3>${t.city.name}, ${t.city.country}</h3>\n\t\t\t\t<h4></h4>\n\t\t\t\t</div>`);let a=["Sunday","Monday","Tuesday","Wednesday","Thursday","Friday","Saturday"],e=["January","February","March","April","May","June","July","August","September","October","November","December"];t.list.forEach(t=>{let i=new Date(1e3*t.dt),n=a[i.getDay()],s=e[i.getMonth()],d=i.getDate(),r=i.getHours()%12,c=i.getMinutes();c=c<10?"0"+c:c;let o=1.8*(parseFloat(t.main.temp_max)-273.15)+32,v=Math.round(o),l=1.8*(parseFloat(t.main.temp_min)-273.15)+32,m=Math.round(l),p=t.weather[0].icon,u=t.weather[0].main,h=t.weather[0].description,y="https://openweathermap.org/img/w/"+p+".png";$("#forecast").append(`<div class="single-forecast-div">\n\t\t\t\t\t<div class="forecast-date">\n\t\t\t\t\t<div class="time-data">\n\t\t\t\t\t<div>${n}, ${s} ${d}</div>\n\t\t\t\t\t\t<div>${r}:${c}</div>\n\t\t\t\t\t\t</div>\n\t\t\t\t\t\t</div>\n\t\t\t\t\t\t<div><img class="icon" src=${y} alt="weather icon"></div>\n\t\t\t\t\t\t<div class="forecast-data">\n\t\t\t\t\t\t<div class="forecast-temp">High: ${v}&#176 | Low: ${m}&#176</div>\n\t\t\t\t\t\t<div class="forecast-humidity">Humidity: ${t.main.humidity}%</div>\n\t\t\t\t\t\t<div class="main-forecast">${u}</div>\n\t\t\t\t\t\t<div class="forecast-description">Description: ${h}</div>\n\t\t\t\t\t\t</div>\n\t\t\t\t\t\t</div>`)})}})})});