function updatingTime(){const e=new Date;let t=e.getHours()%12,n=e.getMinutes(),a=e.getSeconds();a=a<10?"0"+a:a,n=n<10?"0"+n:n;let u=["Sunday","Monday","Tuesday","Wednesday","Thursday","Friday","Saturday"][e.getDay()]+", "+["January","February","March","April","May","June","July","August","September","October","November","December"][e.getMonth()]+" "+e.getDate()+", "+e.getFullYear(),r=t+":"+n+":"+a+" ";document.getElementById("date").innerHTML=u,document.getElementById("time").innerHTML=r}setInterval(updatingTime,1e3);