$(document).ready(()=>{$("#submitCityName").on("click",t=>{t.preventDefault();const e="https://api.openweathermap.org/data/2.5/weather?q="+$("#forcastLocation").val()+"&APPID=94d5b3ebbc302231ae85460cfe0af984";$.ajax({url:e,method:"GET",success:t=>{$("#queryLocation")[0].reset(),$("#currentWeather").empty(),$("#currentWeather").append(`<div id="locationDropdown" class="dropdown-content">\n\t\t\t\t<h3>${t.name}, ${t.sys.country}</h3>\n\t\t\t\t </div>`);let e="https://openweathermap.org/img/w/"+t.weather[0].icon+".png",i=parseFloat(t.main.temp),n=1.8*(i-273.15)+32,a=Math.round(n),r=i-273.15,d=Math.round(r),s=parseFloat(t.sys.sunrise),o=new Date(1e3*s).toLocaleTimeString(),c=parseFloat(t.sys.sunset),v=new Date(1e3*c).toLocaleTimeString(),u=.000621371192*t.visibility,p=Math.round(u),l=.02953*t.main.pressure,h=Math.round(l);$("#currentWeather").append(`<div class="current-info-all">\n\t\t\t\t<div class="current-info-main">\n\t\t\t\t<div>${t.weather[0].main}</div>\n\t\t\t\t\t<div>Temp: ${a}&#176  F / ${d}&#176  C</div>\n\t\t\t\t\t<div>Description: ${t.weather[0].description}</div>\n\t\t\t\t\t</div>\n\t\t\t\t\t<div id="weatherIcon" class="current-icon"><img id="icon" src="${e}" alt="weather icon"></div>\n\t\t\t\t\t<div class="supplemental-current-info">\n\t\t\t\t\t<div>Humidity: ${t.main.humidity} %</div>\n\t\t\t\t\t<div>Pressure: ${h} Inches</div>\n\t\t\t\t\t<div>Visibility: ${p} Miles</div>\n\t\t\t\t\t<div>Sunrise: ${o}</div>\n\t\t\t\t\t<div>Sunset: ${v}</div>\n\t\t\t\t\t</div>\n\t\t\t\t\t</div>`)}})})});