$(document).ready(()=>{$("#submitCityName").on("click",t=>{t.preventDefault();const e="https://api.openweathermap.org/data/2.5/forecast?q="+$("#forcastLocation").val()+"&APPID=94d5b3ebbc302231ae85460cfe0af984";$.ajax({url:e,method:"GET",success:t=>{$("#queryLocation")[0].reset(),$("#currentWeather").empty();let e=["Sunday","Monday","Tuesday","Wednesday","Thursday","Friday","Saturday"],a=["January","February","March","April","May","June","July","August","September","October","November","December"];t.list.forEach(t=>{let i=new Date(1e3*t.dt),d=e[i.getDay()],n=a[i.getMonth()],s=i.getDate(),r=i.getHours()%12,c=i.getMinutes();c=c<10?"0"+c:c;let o=1.8*(parseFloat(t.main.temp_max)-273.15)+32,v=Math.round(o),m=1.8*(parseFloat(t.main.temp_min)-273.15)+32,u=Math.round(m),l=t.weather[0].icon,p=t.weather[0].main,h=t.weather[0].description,y="https://openweathermap.org/img/w/"+l+".png";$("#forecastdropdown").append(`<div class="single-forecast-div">\n\t\t\t\t\t<div class="forecast-date">\n\t\t\t\t\t<div class="time-data">\n\t\t\t\t\t<div>${d}</div>\n\t\t\t\t\t<div>${n} ${s}</div>\n\t\t\t\t\t\t<div>${r}:${c}</div>\n\t\t\t\t\t\t</div>\n\t\t\t\t\t\t</div>\n\t\t\t\t\t\t<div class="icon"><img src=${y} alt="weather icon"></div>\n\t\t\t\t\t\t<div class="forecast-data">\n\t\t\t\t\t\t<div class="forecast-temp">High: ${v}&#176 | Low: ${u}&#176</div>\n\t\t\t\t\t\t<div class="forecast-humidity">Humidity: ${t.main.humidity}%</div>\n\t\t\t\t\t\t<div class="main-forecast">${p}</div>\n\t\t\t\t\t\t<div class="forecast-description">Description: ${h}</div>\n\t\t\t\t\t\t</div>\n\t\t\t\t\t\t</div>`)})}})})});